mapper.select:
SELECT id, parent_id, name, path, component, redirect, perms, type, icon, order_num, hidden, status, level, tree_path, keep_alive, always_show, create_by, create_time, update_by, update_time, remark FROM sys_menu
AND name LIKE CONCAT('%', #{name}, '%')
AND status = #{status}
AND type = #{type}
ORDER BY parent_id, order_num

SELECT DISTINCT m.perms FROM sys_menu m LEFT JOIN sys_role_menu rm ON m.id = rm.menu_id WHERE rm.role_id IN
#{roleId}
AND m.status = 1 AND m.perms IS NOT NULL AND m.perms != ''
